  static defaultMessage = 'this method must be implemented in subclasses';
}

import { BaseError } from './BaseError.js';

/**
* base error class for this package.
*/
export class NrserError extends BaseError {}

import { BaseError } from './BaseError';

/**
* thrown when something is in the wrong state for an operation to succeed.
*/
