import _ from '//src/lodash';
import t from 'tcomb';
import { j } from './string.js';

import * as errors from './errors';

export function match(value, ...clauses) {
  for (let i = 0, len = clauses.length; i < len; i += 2) {
    const pattern = clauses[i];
    const output = clauses[i + 1];

    // console.log({pattern, output});

    if (
      (t.isType(pattern) && pattern.is(value)) ||
      (typeof pattern === 'function' && value instanceof pattern) ||
      value === pattern
    ) {
      if (_.isFunction(output)) {
        return output(value);
      } else {
        return output;
      }
    }
  }

  let message = j`value ${ value } failed to match `;
  for (let i = 0, len = clauses.length; i < len; i += 2) {
    const type = clauses[i];

    if (t.isType(type)) {
      message += t.getTypeName(type);
    } else {
      message += type;
    }

    if (i != len - 1) {
      message += ' | ';
    }
  }

  throw new errors.MatchError(message, {value});
};

// @flow

import _ from '//src/lodash';
import type { $Refinement } from 'tcomb';
import * as errors from '../errors';

const LEVELS = {
  fatal: 0,
  error: 1,
  warn: 2,
  info: 3,
  debug: 4,
  trace: 5,
};

export const LEVEL_NAME_PAD_LENGTH = _.chain(LEVELS)
  .keys()
  .map(name => name.length)
  .max()
  .value();

const LEVEL_RANKS: Array<number> = _.values(LEVELS);

function isLevelName(string: string) {
  return _.has(LEVELS, string);
}

export type LevelName = "fatal" | "error" | "warn" | "info" | "debug" | "trace";

function isLevelRank(number: number) {
  return _.includes(LEVEL_RANKS, number);
}

export type LevelRank = number & $Refinement<typeof isLevelRank>;

export class Level {
  name: LevelName;
  rank: LevelRank;

  static FATAL: Level = new Level({name: 'fatal', rank: LEVELS.fatal});
  static ERROR: Level = new Level({name: 'error', rank: LEVELS.error});
  static WARN:  Level = new Level({name: 'warn',  rank: LEVELS.warn});
  static INFO:  Level = new Level({name: 'info',  rank: LEVELS.info});
  static DEBUG: Level = new Level({name: 'debug', rank: LEVELS.debug});
  static TRACE: Level = new Level({name: 'trace', rank: LEVELS.trace});

  static LEVELS: Array<Level> = [
    Level.FATAL,
    Level.ERROR,
    Level.WARN,
    Level.INFO,
    Level.DEBUG,
    Level.TRACE,
  ];

  static forName(name: LevelName): Level {
    // flow doesn't like dynamic prop access...
    switch (name) {
      case 'fatal':
        return this.FATAL;
      case 'error':
        return this.ERROR;
      case 'warn':
        return this.WARN;
      case 'info':
        return this.INFO;
      case 'debug':
        return this.DEBUG;
      case 'trace':
        return this.TRACE;
      default:
        throw new errors.ValueError(`bad level name: ${ name }`, {name});
    }
  }

  constructor({name, rank}: {name: LevelName, rank: LevelRank}) {
    this.name = name;
    this.rank = rank;
  }
}

import minimatch from 'minimatch';
import _ from '//src/lodash';

import { Level } from './Level';
import type { LevelName } from './Level';

export type SpecQuery = {
  path: string,
  content: Array<*>,
};

export type SpecProps = {
  level: LevelName,
  path?: string,
  content?: string,
};

export class LevelSpec {
  level: Level;
  path: ?string;
  pathPattern: ?string;
  content: ?RegExp;
  _cache: Object;

  static minimatchize(s: string): string {
    return s.replace(/\:/g, '/');
  }

  constructor(props: SpecProps) {
    this.level = Level.forName(props.level);

    if (props.path) {
      this.path = props.path;
      this.pathPattern = this.constructor.minimatchize(props.path)
    }

    if (props.content) {
      this.content = new RegExp(props.content);
    }

    this._cache = {};
  }

  cache(key, getter) {
    if (!_.has(this._cache, key)) {
      this._cache[key] = getter();
    }
    return this._cache[key];
  }

  match(query: SpecQuery): boolean {
    return _.every([
      this.matchPath(query.path),
      this.matchContent(query.content),
    ]);
  }

  matchPath(path: string): boolean {
    // there is no path info so it always matches
    if (!this.pathPattern) {
      return true;
    }

    return this.cache(`path=${ path }`, () => {
      return minimatch(this.constructor.minimatchize(path), this.pathPattern);
    });
  }

  matchContent(content: Array<*>): boolean {
    // there is no content info so it always matches
    if (!this.content) {
      return true;
    }

    // otherwise join all the strings in the message content array and
    // see if the content RegExp matches

    return !!_.filter(content, _.isString).join(' ').match(this.content);
  }
}

/**
* new logger for use with babel-plugin-metalog
* 
* adapted from nrser.Logger, which was adapted from pince
* 
* https://github.com/mad-eye/pince
* 
*/

import _ from '//src/lodash';
import t from 'tcomb';
import minimatch from 'minimatch';

import { IS_NODE, IS_BROWSER } from '../env';
import print from '../print';
import type { NonNegativeInteger } from '../types/number';

import { Level, LEVEL_NAME_PAD_LENGTH } from './Level';
import type { LevelName } from './Level';
import { LevelSpec } from './LevelSpec';
import type { SpecQuery, SpecProps } from './LevelSpec';
import { snapshot } from './snapshot';

// optional requires that may or may not be present

let notifier;
try {
  notifier = require('node-notifier');
} catch (e) {}

let chalk;
try {
  chalk = require('chalk');
} catch (e) {}

// types
// =====

type RefsLabel =  "errorRefs" |
                  "warnRefs" | 
                  "infoRefs" | 
                  "debugRefs" | 
                  "traceRefs";

// type NotifLabel = 

/**
* raw message that the METALOG plugin generates as the argument to `METALOG()`
* (or whatever global function call is configured).
* 
* this is basically just what i found in babel-plugin-trace with a few
* small changes.
*/
type MetalogMessage = {
  label: string, // LevelName,
  filename: string,
  filepath: string,
  content: Array<*>,
  line: number,
  parentPath: Array<string>,
};

/**
* message we craft in the runtime out of the MetalogMessage with properties
* we need to actually output.
*/
type LogMessage = {
  // level of the message
  level: Level,

  // if we should log refs (as `console.log` and friends normally do) instead
  // of trying to snapshot the values
  refs: boolean,

  // if we should try to generate a system notification in addition to logging
  // the message
  notif: boolean,

  // level formatted for output
  formattedLevel: string,

  // datetime of the log call
  date: Date,

  // date formatted for output
  formattedDate: string,

  // the path to the log site that we output,
  // which is <filename>:<parentPath>:<line>
  path: string,

  // ms since last message logged
  delta: ?number,

  // delta formatted for output
  formattedDelta: string,

  // the content from the MetalogMessage
  content: Array<*>,
}

export class Logger {
  lastMessageDate: ?Date;
  specs: Array<LevelSpec>;
  nodeHeaderFormat: string;
  browserHeaderFormat: string;
  dateFormat: string;
  notifTitle: string;

  // constants
  // =========

  static HEADER_FORMAT_TOKENS = {
    '%date':  (message: LogMessage): string => message.formattedDate,
    // '%level': (message: LogMessage) => COLORS[message.level](data.level),
    '%level': (message: LogMessage): string => message.formattedLevel,
    '%delta': (message: LogMessage): string => message.formattedDelta,
    '%path':  (message: LogMessage): string => message.path,
  };

  /**
  * tokens in date format string are replaced with result of calling
  * the function with the date.
  */
  static DATE_FORMAT_TOKENS = {
    'YYYY': (d: Date): string => _.padStart(d.getFullYear(), 4, "0"),
    'MM':   (d: Date): string => _.padStart(d.getMonth() + 1, 2, "0"),
    'DD':   (d: Date): string => _.padStart(d.getDate(), 2, "0"),
    'HH':   (d: Date): string => _.padStart(d.getHours(), 2, "0"),
    'mm':   (d: Date): string => _.padStart(d.getMinutes(), 2, "0"),
    'ss':   (d: Date): string => _.padStart(d.getSeconds(), 2, "0"),
    'SSS':  (d: Date): string => _.padStart(d.getMilliseconds(), 3, "0"),
  };

  static BROWSER_COLORS = {
    error: '#CC0C39', //'#d8292f',
    warn: '#E6781E', // '#d39100',
    info: '#1693A7', // '#337ab7',
    debug: '#00548b',
    trace: '#a3aaae',
  };

  static NODE_COLORS = chalk ? {
    error: chalk.red,
    warn: chalk.yellow,
    info: chalk.blue,
    debug: chalk.cyan,
    trace: chalk.gray,
  } : {
    error: x => x,
    warn: x => x,
    info: x => x,
    debug: x => x,
    trace: x => x,
  };

  // static methods
  // ==============

  static format(
    data: *,
    formatStr: string,
    tokens: {[token: string]: Function},
  ): string {
    let result: string = formatStr;
    _.each(tokens, (formatter, token) => {
      if (_.includes(formatStr, token)) {
        result = result.replace(new RegExp(token, 'g'), formatter(data));
      }
    });
    return result;
  }

  /**
  * format a date according to a format string, see `.DATE_FORMAT_TOKENS` for
  * the available tokens.
  */
  static formatDate(date: Date, formatStr: string): string {
    return this.format(date, formatStr, this.DATE_FORMAT_TOKENS);
  }

  /**
  * format a header according to a format string, see `.HEADER_FORMAT_TOKENS`
  * for the available tokens.
  */
  static formatHeader(message: LogMessage, formatStr: string): string {
    return this.format(message, formatStr, this.HEADER_FORMAT_TOKENS);
  }

  /**
  * the string output of the level.
  */
  static formatLevel(level: Level): string {
    return _.padEnd(
      level.name.toUpperCase(),
      LEVEL_NAME_PAD_LENGTH
    );
  }

  /**
  * formats a delta in ms for output.
  * 
  * when:
  * 
  * -   delta is undefined (first message), returns "+----ms"
  * -   the delta is 9999 or less, returns something like "+0888ms"
  * -   the delta is over 9999, returns "+++++ms"
  */
  static formatDelta(delta: ?NonNegativeInteger): string {
    let digits = '----';

    if (typeof delta !== 'undefined') {
      if (delta > 9999) {
        digits = '++++';

      } else {
        digits = _.padStart(delta, 4, '0');

      }
    }

    return `+${ digits }ms`;
  }

  /**
  * formats the path consisting of filename, parentPath and line for output.
  */
  static formatPath(rawMessage: MetalogMessage): string {
    const path = [rawMessage.filename];

    if (!_.isEmpty(rawMessage.parentPath)) {
      path.push(
        _.map(rawMessage.parentPath, (segment: string) => {
          if (segment.match(/\[anonymous\@\d+\]/)) {
            return "?";
          }
          return segment;
        }).join(':')
      );
    }

    path.push(rawMessage.line);

    return path.join(':');
  }

  // ========

  constructor({
    nodeHeaderFormat = "%date (%delta) %level [%path]",
    browserHeaderFormat = "(%delta) %level [%path]",
    dateFormat = "YYYY-MM-DD HH:mm:ss.SSS",
    notifTitle = 'METALOGGER',
    levelSpecs,
  }: {
    nodeHeaderFormat: string,
    browserHeaderFormat: string,
    dateFormat: string,
    notifTitle: string,
  } = {}) {
    this.nodeHeaderFormat = nodeHeaderFormat;
    this.browserHeaderFormat = browserHeaderFormat;
    this.dateFormat = dateFormat;
    this.notifTitle = notifTitle;
    this.specs = [];

    if (levelSpecs) {
      _.each(levelSpecs, (spec) => this.pushSpec(spec));
    }
  } // constructor

  // instance methods
  // ================

  /**
  * adds a spec to the end of the specs array (least priority).
  * returns the spec.
  */
  pushSpec(spec: LevelSpec | SpecProps): LevelSpec {
    if (!(spec instanceof LevelSpec)) {
      spec = new LevelSpec(spec);
    }

    this.specs.push(spec);

    return spec;
  }

  /**
  * adds a spec to the beginning of the specs (highest priority).
  * returns the spec.
  */
  unshiftSpec(spec: LevelSpec | SpecProps): LevelSpec {
    if (!(spec instanceof LevelSpec)) {
      spec = new LevelSpec(spec);
    }

    this.spec.unshift(spec);

    return spec;
  }

  /**
  * sends a system notification if the node-notifier package is available.
  */
  notify(message: LogMessage) {
    if (notifier) {      
      notifier.notify({
        title: this.notifTitle,
        message: (
          message.formattedLevel + "\n" + 
          _.map(message.content, (obj) => {
            if (typeof obj === 'string') {
              return obj;
            } else {
              return print(obj);
            }
          }).join("\n")
        ),
      });
    }
  }

  /**
  * find the active level spec given spec query properties from a log message, 
  * which is the first spec for which `LevelSpec#match` returns true.
  * 
  * - query: SpecQuery
  * 
  * 
  * returns `undefined` if no spec matches.
  */
  specFor(query: SpecQuery): ?LevelSpec {
    return _.find(this.specs, (spec: LevelSpec) => spec.match(query));
  }

  /**
  * get the level from the spec for a query, if any match.
  */
  levelFor(query: SpecQuery): ?Level {
    const spec: ?LevelSpec = this.specFor(query);

    if (spec) {
      return spec.level;
    }

    return; // undefined
  }

  /**
  * determines if a message should be logged given level and spec query info
  * by finding the applicable level spec and seeing if it's level is 
  */
  shouldLog(level: Level, query : SpecQuery): boolean {
    const currentLevel: ?Level = this.levelFor(query);

    if (!currentLevel) {
      // we don't have any matching level spec so log everything
      return true;
    }

    return level.rank <= currentLevel.rank;
  }

  /**
  * log a message unless filtered by a level spec.
  */
  log(rawMessage: MetalogMessage): boolean {
    let refs: boolean = false;
    let notif: boolean = false;
    let levelName: string = rawMessage.label;

    // plain 'notif' label is logged as 'info' level
    if (levelName === 'notif') {
      notif = true;
      levelName = 'info';
    }

    if (_.includes(levelName, 'Refs')) {
      refs = true;
      levelName = levelName.replace('Refs', '');
    }

    if (_.includes(levelName, 'Notif')) {
      notif = true;
      levelName = levelName.replace('Notif', '');
    }

    const path: string = this.constructor.formatPath(rawMessage);

    const level: Level = Level.forName(levelName);
    const query: SpecQuery = {
      content: rawMessage.content,
      path,
    };

    // bail now if there is a spec filtering out this message
    if (!this.shouldLog(level, query)) {
      return false;
    }

    const now: Date = new Date();
    const delta: ?NonNegativeInteger = this.getDelta(now);
    // set the last message date so `#getDelta` will work
    // do it here so an error outputting won't break `#getDelta`
    this.lastMessageDate = now;

    // now we know we're going to output
    // form the log message
    const logMessage: LogMessage = {
      level,
      refs,
      notif,
      formattedLevel: this.constructor.formatLevel(level),
      date: now,
      formattedDate: this.constructor.formatDate(now, this.dateFormat),
      path,
      delta,
      formattedDelta: this.constructor.formatDelta(delta),
      content: rawMessage.content,
    };

    // do environment-dependent output
    if (IS_NODE) {
      this.logInNode(logMessage);
    } else if (IS_BROWSER) {
      this.logInBrowser(logMessage);
    } else {
      throw new Error("don't seem to be in node or the browser, can't log");
    }

    // signal that we output the log
    return true;
  } // log

  /**
  * gets the ms since the last message was logged, or undefined if it's the
  * first.
  */
  getDelta(now: Date): ?NonNegativeInteger {
    if (this.lastMessageDate) {
      return now - this.lastMessageDate;
    }
  }

  /**
  * get the proper `console.*` function for the log level.
  */
  getConsoleFunction(level: Level): Function {
    if (IS_BROWSER) {
      return console.log;
    }

    let fn: Function;

    switch (level.name) {
      case 'trace':
        return console.debug || console.log;

      case 'debug':
        return console.debug || console.log;

      case 'info': 
        fn = console.info || console.log;
        break;

      case 'warn':
        fn = console.warn || console.log;
        break;

      case 'error':
        fn = console.error || console.log;
        break;

      default:
        throw new TypeError(`bad level name?! ${ level.name }`);
    }

    return fn;
  }

  /**
  * log the message in node, where we don't need to fuck with
  * values vs. references like we do on the browser since it get spits out
  * text at that time. we also have colors there (easily).
  * 
  * gonna try out the `print` npm package for display.
  * 
  * https://www.npmjs.com/package/print
  */
  logInNode(message: LogMessage): void {
    const header: string = this.constructor.NODE_COLORS[message.level.name](
      this.constructor.formatHeader(
        message,
        this.nodeHeaderFormat
      )
    );

    const dumps: Array<string> = _.map(message.content, (value: *): string => {
      if (typeof value === 'string') {
        return value;
      } else {
        return print(value);
      }
    });

    this.getConsoleFunction(message.level).call(console, header, ...dumps);

    // send a notif if needed
    if (message.notif) {
      this.notify(message);
    }
  }

  /**
  * log the message in the browser, where we want to log the actual
  * objects so we can explore them in the console, but usually want to log
  * snapshots of the values as they were at the times they were logged.
  */
  logInBrowser(message: LogMessage): void {
    const header: string = this.constructor.formatHeader(
      message,
      this.browserHeaderFormat
    );

    this.getConsoleFunction(message.level).call(
      console,
      `%c ${ header }`,
      `color: ${ this.constructor.BROWSER_COLORS[message.level.name] };`,
      ...(message.refs ? message.content : snapshot(message.content))
    );
  }

} // class Logger

import { Logger } from './Logger';

export const DEFAULT_LOGGER = new Logger();

if (!global.METALOG) {
  global.METALOG = DEFAULT_LOGGER.log.bind(DEFAULT_LOGGER);
}

export * from './Level';
export * from './LevelSpec';
export * from './Logger';

/**
* take a baseClone of an object so that it can be explored in the browser
* in the state is was **at the time**.
* 
* pretty much lodash's cloneDeep changed to not just return empty objects when
* it can't clone something.
*/

var Stack = require('lodash/_Stack'),
    arrayEach = require('lodash/_arrayEach'),
    assignValue = require('lodash/_assignValue'),
    baseAssign = require('lodash/_baseAssign'),
    cloneBuffer = require('lodash/_cloneBuffer'),
    copyArray = require('lodash/_copyArray'),
    copySymbols = require('lodash/_copySymbols'),
    getAllKeys = require('lodash/_getAllKeys'),
    getTag = require('lodash/_getTag'),
    initCloneArray = require('lodash/_initCloneArray'),
    initCloneByTag = require('lodash/_initCloneByTag'),
    initCloneObject = require('lodash/_initCloneObject'),
    isArray = require('lodash/isArray'),
    isBuffer = require('lodash/isBuffer'),
    isObject = require('lodash/isObject'),
    keys = require('lodash/keys');

/** `Object#toString` result references. */
var argsTag = '[object Arguments]',
    arrayTag = '[object Array]',
    boolTag = '[object Boolean]',
    dateTag = '[object Date]',
    errorTag = '[object Error]',
    funcTag = '[object Function]',
    genTag = '[object GeneratorFunction]',
    mapTag = '[object Map]',
    numberTag = '[object Number]',
    objectTag = '[object Object]',
    regexpTag = '[object RegExp]',
    setTag = '[object Set]',
    stringTag = '[object String]',
    symbolTag = '[object Symbol]',
    weakMapTag = '[object WeakMap]';

var arrayBufferTag = '[object ArrayBuffer]',
    dataViewTag = '[object DataView]',
    float32Tag = '[object Float32Array]',
    float64Tag = '[object Float64Array]',
    int8Tag = '[object Int8Array]',
    int16Tag = '[object Int16Array]',
    int32Tag = '[object Int32Array]',
    uint8Tag = '[object Uint8Array]',
    uint8ClampedTag = '[object Uint8ClampedArray]',
    uint16Tag = '[object Uint16Array]',
    uint32Tag = '[object Uint32Array]';

/** Used to identify `toStringTag` values supported by `_.clone`. */
var cloneableTags = {};
cloneableTags[argsTag] = cloneableTags[arrayTag] =
cloneableTags[arrayBufferTag] = cloneableTags[dataViewTag] =
cloneableTags[boolTag] = cloneableTags[dateTag] =
cloneableTags[float32Tag] = cloneableTags[float64Tag] =
cloneableTags[int8Tag] = cloneableTags[int16Tag] =
cloneableTags[int32Tag] = cloneableTags[mapTag] =
cloneableTags[numberTag] = cloneableTags[objectTag] =
cloneableTags[regexpTag] = cloneableTags[setTag] =
cloneableTags[stringTag] = cloneableTags[symbolTag] =
cloneableTags[uint8Tag] = cloneableTags[uint8ClampedTag] =
cloneableTags[uint16Tag] = cloneableTags[uint32Tag] = true;
cloneableTags[errorTag] = cloneableTags[funcTag] =
cloneableTags[weakMapTag] = false;

/**
* used to wrap an un-clonable value so we know
*/
export class SNAPSHOT_REF {
  constructor(value) {
    this.value = value;
  }
}

/**
 * The base implementation of `_.clone` and `_.cloneDeep` which tracks
 * traversed objects.
 *
 * @private
 * @param {*} value The value to clone.
 * @param {boolean} [isDeep] Specify a deep clone.
 * @param {boolean} [isFull] Specify a clone including symbols.
 * @param {Function} [customizer] The function to customize cloning.
 * @param {string} [key] The key of `value`.
 * @param {Object} [object] The parent object of `value`.
 * @param {Object} [stack] Tracks traversed objects and their clone counterparts.
 * @returns {*} Returns the cloned value.
 */
function baseClone(value, isDeep, isFull, customizer, key, object, stack) {
  var result;
  if (customizer) {
    result = object ? customizer(value, key, object, stack) : customizer(value);
  }
  if (result !== undefined) {
    return result;
  }
  if (!isObject(value)) {
    return value;
  }
  var isArr = isArray(value);
  if (isArr) {
    result = initCloneArray(value);
    if (!isDeep) {
      return copyArray(value, result);
    }
  } else {
    var tag = getTag(value),
        isFunc = tag == funcTag || tag == genTag;

    if (isBuffer(value)) {
      return cloneBuffer(value, isDeep);
    }
    if (tag == objectTag || tag == argsTag || (isFunc && !object)) {
      result = initCloneObject(isFunc ? {} : value);
      if (!isDeep) {
        return copySymbols(value, baseAssign(result, value));
      }
    } else {
      if (!cloneableTags[tag]) {
        return new SNAPSHOT_REF(value);
      }
      result = initCloneByTag(value, tag, baseClone, isDeep);
    }
  }
  // Check for circular references and return its corresponding clone.
  stack || (stack = new Stack);
  var stacked = stack.get(value);
  if (stacked) {
    return stacked;
  }
  stack.set(value, result);

  if (!isArr) {
    var props = isFull ? getAllKeys(value) : keys(value);
  }
  arrayEach(props || value, function(subValue, key) {
    if (props) {
      key = subValue;
      subValue = value[key];
    }
    // Recursively populate clone (susceptible to call stack limits).
    assignValue(result, key, baseClone(subValue, isDeep, isFull, customizer, key, value, stack));
  });
  return result;
}

export function snapshot(value) {
  return baseClone(value, true, true);
}

// @flow

import t from 'tcomb';

import _ from '//src/lodash';
import { j, squish } from '//src/string.js';
import { KeyError } from '//src/errors';
import { eachAsArray } from '//src/array';

import type { KeyPath, Collection }  from '//src/types/collection';
import type { Type } from '//src/types/type';

/**
* like lodash/underscore `groupBy` but the iteratee (called `getGroups` here)
* should return an array of string group names and the value will be added to
* **all** those groups in the result.
*/
export function groupByEach<V>(
  collection: Collection<V>,

  getGroups: (
    value: V,
    index: string | number,
    collection: Collection<V>,
  ) => Array<string>,

  context?: Object
): {[group: string]: Array<V>} {
  // 
  let result: {[group: string]: Array<V>} = {};

  _.each(collection, (value: V, index: string | number): void => {
    const groups: Array<string> = getGroups.call(
      context,
      value,
      index,
      collection
    );

    _.each(groups, (group: string) => {
      if (_.has(result, group)) {
        result[group].push(value);
      } else {
        result[group] = [value];
      }
    });
  });

  return result;
} // groupByEach()


/**
* Set `keyPath` to `value` in `object` only if the key does not exist. The key
* path must be completely absent -- if it is set to `undefined` or `null` 
* an error will still be thrown.
* 
* **Mutates `object`.**
* 
* @param {Object} object
*   Object to insert into.
* 
* @param {KeyPath} keyPath
*   Key path to insert at.
* 
* @param {*} value
*   Value to insert.
* 
* @return {Object}
*   The mutated object.
* 
* @throws {KeyError}
*   If `keyPath` exists in `object`.
*/
export function insert(object: Object, keyPath: KeyPath, value: any): Object {
  if (_.has(object, keyPath)) {
    throw KeyError.squish(`Key path ${ keyPath } exists in object.`, {
      keyPath,
      currentValue: _.get(object, keyPath),
      object
    });
  }

  return _.set(object, keyPath, value);
}


/**
* Shallow merges `objects` together into a new {@link Object} as long as there
* are no conflicting keys.
* 
* @param {...Object} objects
*   Objects to merge.
* 
* @return {Object}
*   New object assembled from `objects`.
* 
* @throws {KeyError}
*   If any of `objects` share keys.
*/
export function assemble(...objects: Array<Object>): Object {
  // Objects(objects);

  const result = {};

  _.each(objects, (object) => {
    _.each(object, (value, key) => {
      if (_.has(result, key)) {
        throw new KeyError(
          j`merge conflict for key ${ key }`,
          {objects}
        );
      }

      result[key] = value;
    });
  });

  return result;
} // assemble()


/**
* gets the value at a key path from an object, throwing `KeyError`
* if the result is `undefined`.
*
* @param {Object} object
*   the object to retrieve from.
* 
* @param {?KeyPath} keyPath
*   the path to the key.
* 
* @param {Object} options
* @property {*} defaultValue
*   Value to default to if `keyPath` is not found.
* 
* @return {*}
*   The value at `keyPath`.
*/
export function need(
  object: Object,
  keyPath?: KeyPath,
  {
    defaultValue,
    type,
  }: {
    defaultValue?: mixed,
    type?: Type,
  } = {}
): * {
  const result = _.isEmpty(keyPath) ? (
    object
  ) : (
    _.get(object, keyPath)
  );

  if (result === undefined) {
    if (defaultValue === undefined) {
      let keyPathStr: string;

      if (keyPath === undefined) {
        keyPathStr = 'undefined';
      } else if (Array.isArray(keyPath)) {
        keyPathStr = keyPath.join('.');
      } else {
        keyPathStr = keyPath;
      }

      throw new KeyError(
        `key ${ keyPathStr } not found`,
        {object, keyPath}
      );
    } else {
      // we're going to use the default value

      // check it's type if one was provided -- this makes it so when type
      // is specified the function will always return something that is of 
      // that type if it succeeds.
      if (type) {
        t.assert(type.is(defaultValue), () => squish`
          needed default value ${ t.assert.stringify(defaultValue) }
          of type '${ typeof defaultValue }'
          to be of type ${ t.getTypeName(type) }
          (looking for missing key path ${ t.assert.stringify(keyPath) })
        `);
      }

      return defaultValue;
    }
  }

  if (type) {
    t.assert(type.is(result), () => squish`
      needed value ${ t.assert.stringify(result) }
      of type '${ typeof result }'
      at key path ${ t.assert.stringify(keyPath) }
      to be of type ${ t.getTypeName(type) }.
    `);
  }

  return result;
}

/**
* A pickier [_.pick][] -- throw a {@link KeyError} if any of `paths` are 
* missing.
* 
* [_.pick]: https://lodash.com/docs/4.17.4#pick
*/
export function procure<V>(
  object: {[key: string]: V},
  paths: string | Array<string>,
  {
    defaultValue,
    type,
  }: {
    defaultValue: mixed,
    type: Type,
  } = {}
): {[key: string]: V} {
  const result = {};

  eachAsArray(paths, (path) => {
    _.set(result, path, need(object, path, {defaultValue, type}));
  });

  return result;
}


/**
* map values but drop keys where the mapped value is undefined.
*/
export function mapDefinedValues<DOMAIN, CODOMAIN>(
  obj: {[key: string]: DOMAIN},
  iteratee: (value: DOMAIN, key: string) => CODOMAIN
): {[key: string]: CODOMAIN} {
  const results: {[key: string]: CODOMAIN} = {};

  _.each(obj, (value: DOMAIN, key: string): void => {
    const result = iteratee(value, key);
    if (result !== undefined) {
      results[key] = result;
    }
  });

  return results;
}

_.mixin({
  insert,
  need,
  assemble,
  procure,
  mapDefinedValues,
});

