class Throws {
  errorClass: ErrorClass;
  pattern: ?RegExp;

  constructor(errorClass: ErrorClass = Error, pattern?: RegExp) {
    this.errorClass = errorClass;
    this.pattern = pattern;
  }

  throwArgs() {
    const args = [this.errorClass];
    if (this.pattern) {
      args.push(this.pattern);
    }
    return args;
  }
}


