import type { $Refinement, $Reify } from 'tcomb';

/**
* tcomb type meta object
* 
* @see http://gcanti.github.io/tcomb/guide/index.html
* @see https://archive.is/3JwrZ
* 
* @see https://github.com/gcanti/tcomb/blob/master/docs/API.md
* @see https://archive.is/C6Iqh
*/
export type Meta = {
  // the tcomb type kind, see their docs / source for details.
  // 
  kind: string,

  // the type name
  // 
  // this can be undefined, like when creating a Struct with t.struct and
  // not prodiving a `name` in the options.
  //
  name?: string,

  // if true, `tcomb/lib/create` will create a instance with `type(value)`.
  // if false (*and* `type` is an object *and* value is not `null`) it will
  // use `new type(value)`
  identity?: boolean,
}

/**
* a tcomb type.
* 
* this is to support flow; if you need to test in runtime use tcomb's t.Type.
* 
* @typedef {Object} Type
*/
export type Type = {
  meta: Meta,
  displayName: string,
  is: (value: any) => boolean,
}

export const tType = (({}: any): $Reify<Type>);

/**
* tcomb props - an object mapping property names to their tcomb type.
*/
export type Props = {[name: string]: Type};

import t from 'tcomb';
import _ from '//src/lodash';

import { NonEmptyString, UpperCaseString } from './string.js';
import { nonEmptyList } from './list.js';

/**
* create a type that just wraps a specific value. `is()` uses `===`.
*/
export function value(value, name) {
  return t.irreducible(
    name || `Value<${ value  }>`,
    v => v === value,
  );
}

/**
* `values()` creates a union type for each of the values in the provided
* object.
*/
export function values(obj, name) {
  const valueTypes = _.mapValues(t.Object(obj), (v) => value(v));
  const type = t.union(_.values(valueTypes));
  type.types = valueTypes;
  type.values = {};

  _.each(obj, (v, k) => {
    UpperCaseString(k);

    // shouldn't happen since props are not upper case
    if (_.has(type, k)) {
      throw new errors.ValueError(squish(`
        can\'t provide key ' + k + ' because union type defines the property
      `));
    }

    // convenient accessor, like VALUES.X
    type[k] = v;

    // nice for iteration of the values
    type.values[k] = v;
  });

  return type;
}

values.of = function (array) {
  nonEmptyList(NonEmptyString)(array);

  var obj = {};

  for (var i = 0, len = array.length; i < len; i++) {
    obj[array[i].toUpperCase()] = array[i];
  }

  return values(obj);
};
